
"use strict";window.onload=function(){var imgs=document.getElementsByClassName("slider")[0].getElementsByClassName("img");var len=imgs.length;var index=0;setInterval(function(){Object.keys(imgs).map(function(i){if(i!=index)imgs.item(i).dataset.image="normal";});imgs.item(index).dataset.image="top";index++;if(index>=len)index=0;},2500);};"use strict";window.onload=function(){var modals=document.getElementsByClassName("modal");var trigger=document.getElementsByClassName("modal-trigger");var closes=document.getElementsByClassName("modal-close");if(closes){Object.keys(closes).map(function(i){closes.item(i).addEventListener('click',function(){closeModal(modals.item(i).dataset.modal);});});}
if(trigger){Object.keys(trigger).map(function(i){trigger.item(i).addEventListener('click',function(){openModal(trigger.item(i).dataset.modal);});});}
if(modals){Object.keys(modals).map(function(i){modals.item(i).addEventListener('click',function(e){var target=e.target.className.toString();if(target==="modal-content")return true;else if(target==="modal")closeModal(modals.item(i).dataset.modal);});});}
function openModal(modal){Object.keys(modals).map(function(i){if(modals.item(i).dataset.modal===modal)modals.item(i).dataset.status="show";});}
function closeModal(modal){Object.keys(modals).map(function(i){if(modals.item(i).dataset.modal===modal)modals.item(i).dataset.status="hidden";});}};"use strict";document.addEventListener("DOMContentLoaded",function(){var overlay=document.getElementById("overlay");var navigations=document.getElementsByClassName("nav-link");if(overlay){overlay.addEventListener('click',function(){closeNav();});}
if(navigations){Object.keys(navigations).map(function(i){navigations.item(i).onclick=closeNav;});}
function closeNav(){document.getElementById("btn-menu").checked=false;}});